package myz.graduation_design.httpservice;import java.util.List;import myz.graduation_design.Model.MovieRes;import rx.Observable;/** * Created by HP on 2017/11/7. */public class UserLoader extends ObjectLoader {    private UserService mUserService;    public UserLoader(){        mUserService = RetrofitServiceManager.getInstance().create(UserService.class);    }    public Observable<List<MovieRes>> getPlayingMovie(){        return observe(mUserService.getPlayingMovie(0,3));    }}